createDb.sql_createTable:
create table if not exists tb_user(
user_id Integer primary key,
name TEXT,
password TEXT,
lastLoginTime TEXT
);

create table if not exists tb_photo(
path TEXT,
time TEXT,
sendTime TEXT
);

create table if not exists tb_photo(
path TEXT,
time TEXT,
sendTime TEXT,
size TEXT
);

